// Read the current translations file
const translationsPath = path.join(__dirname, '..', 'src', 'constants', 'translations.ts');
let content = fs.readFileSync(translationsPath, 'utf8');

// Find the zhTWBase object and replace it
const zhTWBaseStart = content.indexOf('const zhTWBase = {');
const zhTWBaseEnd = content.indexOf('};', zhTWBaseStart) + 2;

if (zhTWBaseStart === -1) {
  console.error('Could not find zhTWBase object in translations.ts');
  process.exit(1);
}

// Generate the new zhTWBase string
const zhTWBaseString = `const zhTWBase = ${JSON.stringify(zhTWComplete, null, 2)};`;

// Replace the old zhTWBase with the new one
const newContent = content.substring(0, zhTWBaseStart) + zhTWBaseString + content.substring(zhTWBaseEnd);

// Write the updated content back to the file
fs.writeFileSync(translationsPath, newContent, 'utf8');

